import React, { useState,useEffect } from "react"
import facade from "./components/apiFacade";
import 'bootstrap/dist/css/bootstrap.min.css'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams,
  useRouteMatch
} from "react-router-dom";

function LogIn({ login }) {
  const init = { username: "", password: "" };
  const [loginCredentials, setLoginCredentials] = useState(init);

  const performLogin = (evt) => {
    evt.preventDefault();
    login(loginCredentials.username, loginCredentials.password);
  }
  const onChange = (evt) => {
    setLoginCredentials({ ...loginCredentials,[evt.target.id]: evt.target.value })
  }

  return (
    <div class="row">
      <div class="col" />
      <div class="col-lg">
      <h2>Login</h2>
      <form onChange={onChange} >
        <input class="form-control" placeholder="User Name" id="username" />
        <br />
        <input type="password" class="form-control" placeholder="Password" id="password" />
        <br />
        <button class="btn btn-primary" onClick={performLogin}>Login</button>
      </form>
      </div>
      <div class="col" />
    </div>
  )

}
function LoggedIn(props) {


  useEffect(() => {
    facade.fetchData()
    .then(data=> props.setDataFromServer(data));
  }, [])

  return (
    <div>
      <h2>Data Received from server</h2>
      <h3>{props.dataFromServer.msg}</h3>
    </div>
  )

}

function App() {
  const [dataFromServer, setDataFromServer] = useState("Loading...")
  const [loggedIn, setLoggedIn] = useState(false)

  const logout = () => {
    facade.logout();
    setLoggedIn(false);
  } 

  const login = (user, pass) => {
    facade.login(user,pass)
    .then(res =>setLoggedIn(true));
  } 

  return (
    <Router>
      <ul>
        <li>
          <Link to="/">Home</Link>
        </li>
        {!dataFromServer ? "" :
          <li>
          <Link to="/user">User endpoint demo</Link>
          </li>

        }

        <li>
          <Link to="/admin">Admin endpoint demo</Link>
        </li>
        <li>
          <Link to="/external">external server fetch demo</Link>
        </li>
      </ul>
      <br />
      <br />
      <br />
      <Switch>
        <Route exact path="/">
        {!loggedIn ? (<LogIn login={login} />) :
          (<div>
            <LoggedIn dataFromServer={dataFromServer} setDataFromServer={setDataFromServer}/>
            <button onClick={logout}>Logout</button>
          </div>)}
        </Route>
        <Route path="/user">
          <p></p>
        </Route>
        <Route path="/admin">
          <p>Admin stuff</p>
        </Route>
        <Route path="/external">
          <p>External stuff</p>
        </Route>
      </Switch>

    </Router>
  )

}
export default App;
